% Task 2: plotting and max temp% Note: must run the Finite Difference program first to generate T matrix% Find the index within the t vector of the desired timedesiredt = 1.2;index = int8((desiredt/deltat)+1);tempvec = T(index,:);maxtemp = max(tempvec);maxindex = find(tempvec == maxtemp);% Print necessary parameters to screendt = deltatdx = deltaxmax_temp = maxtempxmax = xvec(maxindex)clffigure(1)plot(xvec, tempvec, '.')grid onaxis([0 1 295 305])xlabel('Distance Along Rod')ylabel('Temperature (degrees)')saveas(1,'task2fig.png')