% Task 3: Plot T vs x for multiple time steps% Note: must run Finite Difference script prior to Task 3 scriptclffigure(1)plot(xvec, T(1,:), '.', xvec, T(((1/deltat)+1),:), '.', xvec, T(((2/deltat)+1),:),'.', xvec, T(((3/deltat)+1),:), '.', xvec, T(((4/deltat)+1),:), '.',xvec, T(((5/deltat)+1),:),'.')grid onaxis([0 1 295 310])xlabel('Distance Along Rod')ylabel('Temperature (degrees)')legend('t = 0', 't = 1 s', 't = 2 s', 't = 3 s', 't = 4 s', 't = 5 s','location','southeast')saveas(1,'task3fig.png')