% Task 6 & 7 Script% Note: In order to run this script, you must have the following variables active in your workspace: T(nxm matrix), deltat, deltax% Initial Condition:theta(1) = 300;% Constant:beta = 0.01;[r,c] = size(T);for i = 1:r-2  T1 = T(i,1);  T2 = T(i,2);    theta(i+1) = theta(i) - (beta*deltat*((theta(i)-300)^2)) + ((deltat/deltax)*T2) - ((deltat/deltax)*T1);endfor maxtemp = max(theta)% Task 7: plottingplot(tvec, theta, '.')grid onxlabel('Time (s)')ylabel('Temperature of Fluid')saveas(1, 'task6fig.png')  