% Task 9 Script% This script requires the following variables to be loaded to the workspace: T (mxn matrix), tvec, xvecclear, clc% Load variables to workspaceload Tempmatrix.mat;load tvec.mat;load xvec.mat;for j = 1:length(tvec)i = 1;hsum = 0;  Tempvec = T(j,:);  for i = 2:length(xvec)-1    hsum = hsum + ((xvec(i+1) - xvec(i))/6) * (Tempvec(i+1) + (4*Tempvec(i)) + Tempvec(i-1));    H(j) = hsum;  endforendfor%plot(tvec, H,'.')%grid on%xlabel('Time (s)')%ylabel('Total Heat in Rod')%h = gcf;%saveas(h, 'task9plot.png')% Report heat at t = 1.2;desiredt = 1.2;deltat = tvec(2)-tvec(1)index = int16((desiredt/deltat) + 1);StoredHeat = H(index)